INSERT INTO `tbl_item_input` VALUES ('8a8a82695c5bf692015c5c1834320004', '2017-05-31 09:21:49', '0', '2017-05-31 14:31:23', '8', 'Solution008InputResolver', 'public class Solution008InputResolver {\r\n    public ListNode resolve(String param) {\r\n        param=param.replace(\"[\",\"\").replace(\"]\",\"\");\r\n        String[] arr=param.split(\",\");\r\n        ListNode head=new ListNode(Integer.valueOf(arr[0]).intValue());\r\n        ListNode ret=head;\r\n        for(int i=1;i<arr.length;i++){\r\n            ListNode node=new  ListNode(Integer.valueOf(arr[i]).intValue());\r\n            head.next=node;\r\n            head=node;\r\n        }\r\n        return ret;\r\n    }\r\n}\r\n\r\n\r\n/*public class ListNode {\r\n     int val;\r\n     ListNode next;\r\n     ListNode(int x) { val = x; }\r\n}*/', '0', '402880e55c597d0c015c59a856730000', 'resolve', '0,1', 'ListNode');
